declare module '*.scss' {
    const css: { [key: string]: string };
    export default css;
  }
  declare module '*.sass' {
    const css: { [key: string]: string };
    export default css;
  }
  declare module 'react-markup';

declare module "*.png" {
    const value: string;
    export = value;
}

declare module "*.webp" {
   const value: string;
   export = value;
}
declare module "*.jpg" {
   const value: string;
    export = value;
}

declare module "*.jpeg" {
  const value: string;
   export = value;
}